
// This file is automatically generated. Do not edit it directly.
import { createClient } from '@supabase/supabase-js';
import type { Database } from './types';
import { toast } from "@/hooks/use-toast";

// Default to project URL without keys - will be updated by fetching from edge function
let SUPABASE_URL = "https://gsvyxsddmddipeoduyys.supabase.co";
let SUPABASE_PUBLISHABLE_KEY = "";

// Create Supabase client with dynamic configuration
export const createSupabaseClient = async () => {
  try {
    // Fetch configuration from edge function
    console.log("Fetching Supabase configuration from edge function...");
    const response = await fetch('https://gsvyxsddmddipeoduyys.supabase.co/functions/v1/get-config', {
      method: 'GET',
      headers: {
        'Content-Type': 'application/json'
      }
    });
    
    if (!response.ok) {
      const errorData = await response.json().catch(() => ({}));
      console.error("Config fetch error:", response.status, response.statusText, errorData);
      throw new Error(`Failed to get configuration: ${response.status} ${response.statusText}`);
    }
    
    const config = await response.json();
    console.log("Config loaded successfully");
    
    // Only update URL if provided (otherwise keep default)
    if (config.supabaseUrl) {
      SUPABASE_URL = config.supabaseUrl;
    }
    
    SUPABASE_PUBLISHABLE_KEY = config.supabaseAnonKey;
    
    if (!SUPABASE_PUBLISHABLE_KEY) {
      console.error("No Supabase anon key available");
      throw new Error("Failed to initialize Supabase client: No authentication key available");
    }
    
    console.log("Creating Supabase client with URL:", SUPABASE_URL);
    
    return createClient<Database>(SUPABASE_URL, SUPABASE_PUBLISHABLE_KEY, {
      auth: {
        persistSession: true,
        autoRefreshToken: true,
        storage: localStorage,
        storageKey: 'aafairshare-auth',
        detectSessionInUrl: false,
        flowType: 'implicit'
      },
      global: {
        headers: {
          'Content-Type': 'application/json',
        },
        fetch: (url, options) => {
          return fetch(url, {
            ...options,
            signal: options?.signal || AbortSignal.timeout(30000)
          }).catch(error => {
            console.error("Fetch error:", error);
            throw error;
          });
        }
      },
      realtime: {
        heartbeatIntervalMs: 30000
      }
    });
  } catch (error) {
    console.error("Error initializing Supabase client:", error);
    toast({
      title: "Connection Error",
      description: "Failed to initialize database connection. Please try again later.",
      variant: "destructive"
    });
    throw error;
  }
};

// Initialize client
let supabaseClientPromise: Promise<ReturnType<typeof createClient<Database>>> | null = null;

// Lazy-loaded Supabase client - only initialize when first needed
export const getSupabase = async () => {
  if (!supabaseClientPromise) {
    supabaseClientPromise = createSupabaseClient();
  }
  return supabaseClientPromise;
};

// Create a temporary client instance for non-async contexts
